                ['name' => '–û–ø—ã—Ç —Ä–∞–±–æ—Ç—ã', 'key' => 'experience', 'type' => 'select', 'is_required' => true, 'options' => json_encode(["–ë–µ–∑ –æ–ø—ã—Ç–∞", "–ú–µ–Ω—å—à–µ –≥–æ–¥–∞", "1-3 –≥–æ–¥–∞", "3-5 –ª–µ—Ç", "–ë–æ–ª–µ–µ 5 –ª–µ—Ç"])],
                ['name' => '–ì—Ä–∞—Ñ–∏–∫ —Ä–∞–±–æ—Ç—ã', 'key' => 'schedule', 'type' => 'select', 'is_required' => false, 'options' => json_encode(["–ü–æ–ª–Ω—ã–π –¥–µ–Ω—å", "–°–º–µ–Ω–Ω—ã–π –≥—Ä–∞—Ñ–∏–∫", "–ì–∏–±–∫–∏–π –≥—Ä–∞—Ñ–∏–∫", "–£–¥–∞–ª–µ–Ω–Ω–∞—è —Ä–∞–±–æ—Ç–∞", "–í–∞—Ö—Ç–æ–≤—ã–π –º–µ—Ç–æ–¥", "–ù–µ–ø–æ–ª–Ω—ã–π –¥–µ–Ω—å"])],
                ['name' => '–ñ–µ–ª–∞–µ–º–∞—è –∑–∞—Ä–ø–ª–∞—Ç–∞', 'key' => 'salary', 'type' => 'number', 'is_required' => false, 'options' => null],
                ['name' => '–û–±—Ä–∞–∑–æ–≤–∞–Ω–∏–µ', 'key' => 'education', 'type' => 'select', 'is_required' => false, 'options' => json_encode(["–°—Ä–µ–¥–Ω–µ–µ", "–°—Ä–µ–¥–Ω–µ–µ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ–µ", "–ù–µ–ø–æ–ª–Ω–æ–µ –≤—ã—Å—à–µ–µ", "–í—ã—Å—à–µ–µ", "–î–≤–∞ –∏ –±–æ–ª–µ–µ –≤—ã—Å—à–∏—Ö", "–£—á–µ–Ω–∞—è —Å—Ç–µ–ø–µ–Ω—å"])],
