            'beauty-and-health' => [
                ['name' => '–ö–∞—Ç–µ–≥–æ—Ä–∏—è', 'key' => 'beauty_category', 'type' => 'select', 'is_required' => true, 'options' => json_encode(["–ö–æ—Å–º–µ—Ç–∏–∫–∞", "–ü–∞—Ä—Ñ—é–º–µ—Ä–∏—è", "–°—Ä–µ–¥—Å—Ç–≤–∞ –ø–æ —É—Ö–æ–¥—É", "–ë–∏–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–µ –¥–æ–±–∞–≤–∫–∏", "–ú–µ–¥—Ç–µ—Ö–Ω–∏–∫–∞", "–ú–∞—Å—Å–∞–∂–µ—Ä—ã", "–û—Ä—Ç–æ–ø–µ–¥–∏—è"])],
                ['name' => '–°–æ—Å—Ç–æ—è–Ω–∏–µ', 'key' => 'condition', 'type' => 'select', 'is_required' => true, 'options' => json_encode(["–ù–æ–≤—ã–π", "–ë/—É", "–í –∑–∞–ø–µ—á–∞—Ç–∞–Ω–Ω–æ–π —É–ø–∞–∫–æ–≤–∫–µ"])],
                ['name' => '–ú–∞—Ä–∫–∞', 'key' => 'brand', 'type' => 'text', 'is_required' => false, 'options' => null],
